import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet } from 'react-native';

const ProductScreen = () => {
  const [produits, setProduits] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://10.60.136.83:8080/api/produit');
        if (!response.ok) {
          throw new Error('Failed to fetch products with status ' + response.status);
        }
        const data = await response.json();
        setProduits(data);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError(error.message);
      }
    };

    fetchProducts();
  }, []);

  const renderItem = ({ item }) => (
    <View style={styles.itemContainer}>
      <Text style={styles.itemText}>{item.id_produit}</Text>
      <Text style={styles.itemText}>{item.nom_produit}</Text>
      <Text style={styles.itemText}>{item.reference}</Text>
      <Text style={styles.itemText}>{`${item.prix_vente_unitaire} €`}</Text>
      <Text style={styles.itemText}>{item.quantite_stock}</Text>
      <Text style={styles.itemText}>{item.taille_disponible}</Text>
      <Text style={styles.itemText}>{item.couleur_disponible}</Text>
      <Text style={styles.itemText}>{item.id_categorie}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      {error ? <Text style={styles.errorText}>Error: {error}</Text> : null}
      <View style={styles.headerContainer}>
        <Text style={styles.headerText}>ID</Text>
        <Text style={styles.headerText}>Nom</Text>
        <Text style={styles.headerText}>Référence</Text>
        <Text style={styles.headerText}>Prix</Text>
        <Text style={styles.headerText}>Stock</Text>
        <Text style={styles.headerText}>Taille</Text>
        <Text style={styles.headerText}>Couleur</Text>
        <Text style={styles.headerText}>Catégorie</Text>
      </View>
      <FlatList
        data={produits}
        keyExtractor={item => item.id_produit.toString()}
        renderItem={renderItem}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
    backgroundColor: '#f5f5f5',
  },
  headerContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    backgroundColor: '#e1e1e1',
    paddingVertical: 10,
    paddingHorizontal: 5,
    marginBottom: 5,
  },
  headerText: {
    flex: 1,
    fontSize: 14,
    fontWeight: 'bold',
    textAlign: 'center',
    color: '#000',
  },
  itemContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 10,
    paddingHorizontal: 5,
    backgroundColor: 'white',
    marginBottom: 5,
  },
  itemText: {
    flex: 1,
    fontSize: 12, // Smaller font size for better readability on phones
    textAlign: 'center',
    color: '#333',
  },
  errorText: {
    color: 'red',
    fontSize: 16,
    textAlign: 'center',
  }
});

export default ProductScreen;
