import React, { useState, useEffect } from 'react';

function Products() {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null); // Add error state

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://10.60.136.83:8080/api/produits');
        if (!response.ok) {
          throw new Error('Réponse pas ok : ' + response.statusText);
        }
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error('ça veux pas mec ! :  ', error);
        setError(error); 
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>Produits</h1>
      {error && <p>Error: {error.message}</p>} 
      <ul>
        {products.map(product => (
          <li key={product.id}>
            {product.nom} - {product.description}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Products;
