import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet } from 'react-native';

const ProductScreen = () => {
  const [produits, setProduits] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://10.60.136.83:8080/api/produit');
        if (!response.ok) {
          throw new Error('Failed to fetch products with status ' + response.status);
        }
        const data = await response.json();
        setProduits(data);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError(error.message);
      }
    };

    fetchProducts();
  }, []);

  const renderItem = ({ item }) => (
    <View style={styles.itemContainer}>
      <Text style={styles.itemText}>{`ID: ${item.id_produit}`}</Text>
      <Text style={styles.itemText}>{`Nom: ${item.nom_produit}`}</Text>
      <Text style={styles.itemText}>{`Référence: ${item.reference}`}</Text>
      <Text style={styles.itemText}>{`Prix: ${item.prix_vente_unitaire} €`}</Text>
      <Text style={styles.itemText}>{`Stock: ${item.quantite_stock}`}</Text>
      <Text style={styles.itemText}>{`Taille(s): ${item.taille_disponible}`}</Text>
      <Text style={styles.itemText}>{`Couleur(s): ${item.couleur_disponible}`}</Text>
      <Text style={styles.itemText}>{`Catégorie: ${item.id_categorie}`}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      {error ? <Text style={styles.errorText}>Error: {error}</Text> : null}
      <FlatList
        data={produits}
        keyExtractor={item => item.id_produit.toString()}
        renderItem={renderItem}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
    backgroundColor: '#f5f5f5',
  },
  itemContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 10,
    marginVertical: 5,
    backgroundColor: 'white',
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
  },
  itemText: {
    flex: 1,
    fontSize: 14,
    color: '#333',
  },
  errorText: {
    color: 'red',
    fontSize: 16,
    textAlign: 'center',
  }
});

export default ProductScreen;
